
public class Sports {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int sport = 3;
		String schoolSport;
		switch (sport) {
		case 1:
			schoolSport= "Basketball";
			break;
		case 2:
			schoolSport= "Football";
			break;
		case 3:
			schoolSport= "Swimming";
			break;
		default:
				schoolSport= "Invalid";
		}
		System.out.println(schoolSport);
	}

}
